function getRandomEmoji() {
  return emojis[Math.floor(Math.random() * emojis.length)];
}


function mitchifyText(text) {
  const textArray = text.split(" ");
  const returnArray = [];

  for (let i = 0; i < textArray.length; i++) {
    const randomNumber = Math.random();

    if (randomNumber < 0.4) {
      returnArray.push(textArray[i].toUpperCase());
    } else if (randomNumber < 0.6) {
      returnArray.push('#' + textArray[i]);
    } else if (randomNumber < 0.8) {
      returnArray.push(textArray[i] + ' ' + getRandomEmoji() + '\n');
    } else {
      returnArray.push(textArray[i]);
    }
  }
  
  returnArray.push(getRandomEmoji());
  return returnArray.join(" ");
}

class App extends Component {
  constructor(props) {
    super(props);

    this.handleChange = this.handleChange.bind(this);
    this.onClick = this.onClick.bind(this);

    this.state = {
      originalTweet: '',
      mitchifiedTweet: ''
    }
  }

  handleChange(originalTweet) {
    this.setState({ originalTweet });
  }

  onClick() {
    const mitchifiedTweet = mitchifyText(this.state.originalTweet);
    this.setState({ mitchifiedTweet });
  }

  render() {
    const originalTweet = this.state.originalTweet;
    const mitchifiedTweet = this.state.mitchifiedTweet;

    return (
      <div className="App">
        <div className="main-header-div">
          <Header as='h1' color='green' textAlign='center'>Tweet Mitchifier™</Header>
        </div>
        <InputTweet
          originalTweet={originalTweet}
          onChange={this.handleChange}
        />
        <Button
          color='green'
          content='Mitchify™'
          icon='heart'
          onClick={this.onClick}
        />
        <OutputTweet
          mitchifiedTweet={mitchifiedTweet}
        />
        <div className="App-logo-div">
          <Image src={logo} className="App-logo" alt="logo" size='small' centered />
        </div>
        <a
          className="App-link"
          href="https://twitter.com/Mitch_Kosowski"
          target="_blank"
          rel="noopener noreferrer"
        >
          -@Mitch_Kosowski #GreatestTraderOfALLTIME
        <span role="img" aria-label="Flexed Biceps"> 💪</span>
          <span role="img" aria-label="Smiling Face With Sunglasses">😎</span>
          <span role="img" aria-label="Thumbs Up">👍</span>
        </a>
      </div>
    );
  }
}

export default App;
